mapper.update:
update member 
set(name = #{name}, idnum= #{idnum},gender= #{gender}, tech_grade= #{tech_grade}, hire_type = #{hire_type}, job= #{job}, name_eng = #{name_eng},name_chn = #{name_chn}
, picture =  #{picture}, birth= #{birth}, married =  #{married}, career =  #{career},salary_type= #{salary_type}, address= #{address},contact =  #{contact}, email =  #{email},
alcohol =  #{alcohol})
where id = #{id}

mapper.select:
select *
from (select rownum num, m.*
from (select * from member) m)
where num between #{beginList} and #{endList}
